sys_script_include.script:
function HtmlRenderPageParcelLabel()
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	var strHtml = '';

	var strParcel = GetStringValue(RP.getParameterValue('parcel'));
	var grParcel = GetParcelRecord(strParcel);
	if (IsValidRecord(grParcel) == false)
	{
		//strHtml += '<script>top.window.href = "'+GetInstanceURL()+'";</script>';
		strHtml += '<p style="text-color: red;">ERROR!</p>';
		return strHtml;
	}

	var strUrl = GetParcelScanPageURL()+'?parcel='+grParcel.sys_id;

	strHtml += '<div style="font-size: 900%;">' + grParcel.number + '</div>';

	strHtml += '<div style="font-size: 500%;">MEDICAL SUPPLIES</div>';

	strHtml += '<div id="qrcode" style="width:300px; height:300px; margin-top:15px;" url="'+strUrl+'"></div>';

	return strHtml;
}
